import React from "react";
import Header from "./Header";
import Button from "@material-ui/core/Button";
import AddIcon from "@material-ui/icons/Add";
import DeleteOutlineIcon from "@material-ui/icons//DeleteOutline";
import EditIcon from "@material-ui/icons//Edit";
import AddPhotoAlternateIcon from "@material-ui/icons//AddPhotoAlternate";
import Footer from "./Footer";
import { useState, useEffect } from "react";

const App = () => {
  const getLocalStorage = () => {
    let list = localStorage.getItem("lists1");
    // console.log(list);
    if (list) {
      return JSON.parse(localStorage.getItem("lists1"));
    } else {
      return [];
    }
  };
  const [note, setNote] = useState({
    title: "",
    content: "",
    image: "",
  });
  const [addItem, setAddItem] = useState(getLocalStorage([]));
  const [toogle, setToggle] = useState(true);
  const [editData, setEditData] = useState(null);
  const [open, setOpen] = useState(false);
  const [search, setSearch] = useState("");
  const [searchResult, setSearchResult] = useState([]);
  const [img, setImg] = useState(null);
  const [imageUrl, setImageUrl] = useState(null);

  const inputEvent = (event) => {
    // const { name, value } = event.target;
    const name = event.target.name;
    const value = event.target.value;

    setNote((oldData) => {
      return {
        ...oldData,
        [name]: value,
      };
    });
  };
  const addEvent = () => {
    if (note && !toogle) {
      setAddItem(
        addItem.map((elem) => {
          if (elem.id === editData) {
            return { ...elem, name: note };
          }
          return elem;
        })
      );
      setToggle(true);
      setNote({
        title: "",
        content: "",
      });
      setEditData(null);
      setOpen(false);
    } else {
      setAddItem((oldData) => {
        const allData = { id: new Date().getTime().toString(), name: note };
        return [...oldData, allData];
      });
      setNote({
        title: "",
        content: "",
      });
    }
  };

  const deleteEvent = (id) => {
    setAddItem((preData) => {
      return preData.filter((elem) => {
        return id !== elem.id;
      });
    });
  };
  const editEvent = (id) => {
    const editData = addItem.find((elem) => {
      return elem.id === id;
    });
    // console.log(editData);
    setToggle(false);
    setNote(editData.name);
    setEditData(id);
    setOpen(true);
  };
  const setExpand = () => {
    setOpen(true);
  };
  const backToNormal = () => {
    setOpen(false);
  };

  const searchEvent = (event) => {
    const data = event.target.value;
    // console.log(data);
    setSearch(data);

    if (data !== "") {
      const searchNotes = addItem.filter((elem) => {
        const allSearchData = {
          tempTitle: elem.name.title,
          tempData: elem.name.content,
        };
        // console.log(allSearchData);
        return Object.values(allSearchData)
          .join("")
          .toLowerCase()
          .includes(data.toLowerCase());
      });
      // console.log(searchNotes);

      setSearchResult(searchNotes);
    } else {
      setSearchResult(addItem);
      setAddItem(addItem);
      // console.log(addItem);
    }
  };

  const uploadImg = (event) => {
    setImg(event.target.files[0]);
    // console.log(event.target.files[0]);
  };
  useEffect(() => {
    if (img) {
      setImageUrl(URL.createObjectURL(img));
    }
  }, [img]);

  useEffect(() => {
    localStorage.setItem("lists1", JSON.stringify(addItem));
  }, [addItem]);

  return (
    <>
      <Header />
      <div className="main_note" onDoubleClick={backToNormal}>
        <input
          type="search"
          id="gsearch"
          placeholder="Search Notes"
          onChange={searchEvent}
          value={search}
          autoComplete="off"
        />
        <form>
          {open ? (
            <input
              type="text"
              name="title"
              value={note.title}
              onChange={inputEvent}
              placeholder="Title"
              autoComplete="off"
            />
          ) : null}

          <textarea
            type="text"
            placeholder="Write  a note..."
            name="content"
            value={note.content}
            onChange={inputEvent}
            onClick={setExpand}
          ></textarea>

          {toogle ? (
            <Button title="AddNote" onClick={addEvent}>
              <AddIcon className="plus_sign" />
            </Button>
          ) : (
            <Button onClick={addEvent}>
              <EditIcon className="plus_sign" />
            </Button>
          )}
          <div>
            <input
              accept="image/*"
              type="file"
              id="select-image"
              style={{ display: "none" }}
              onChange={inputEvent}
              value={note.image}
              name="image"
            />
            <label htmlFor="select-image">
              <Button variant="contained" color="primary" component="span">
                <AddPhotoAlternateIcon className="plus_sign" />
              </Button>
            </label>
          </div>
        </form>
      </div>

      {search.length === 0
        ? addItem.map((curVal) => {
            return (
              <div className="note" key={curVal.id}>
                <h1>{curVal.name.title}</h1>
                <br />
                <p>{curVal.name.content}</p>
                <button
                  className="btn"
                  title="DeleteItem"
                  onClick={() => deleteEvent(curVal.id)}
                >
                  <DeleteOutlineIcon className="deleteIcon" />
                </button>
                <button
                  className="btnEdit"
                  title="EditNote"
                  onClick={() => editEvent(curVal.id)}
                >
                  <EditIcon className="editIcon" />
                </button>
                <img src={imageUrl} height="100px" />
              </div>
            );
          })
        : searchResult.map((curVal) => {
            return (
              <div className="note" key={curVal.id}>
                <h1>{curVal.name.title}</h1>
                <br />
                <p>{curVal.name.content}</p>
                <button
                  className="btn"
                  title="DeleteItem"
                  onClick={() => deleteEvent(curVal.id)}
                >
                  <DeleteOutlineIcon className="deleteIcon" />
                </button>
                <button
                  className="btnEdit"
                  title="EditNote"
                  onClick={() => editEvent(curVal.id)}
                >
                  <EditIcon className="editIcon" />
                </button>
              </div>
            );
          })}

      {/* <Footer /> */}
    </>
  );
};

export default App;
