import React, { useState } from "react";

const Image = () => {
  const [img, setImg] = useState(null);
  const [allimg, setAllimg] = useState([]);
  const inputEvent = (event) => {
    setImg(event.target.files[0]);
  };
  useEffect(() => {
    if (img) {
      setImageUrl(URL.createObjectURL(img));
    }
  }, [img]);
  return (
    <>
      <input
        accept="image/*"
        type="file"
        id="select-image"
        onChange={inputEvent}
        name="image"
        value={img}
      />
      <img src={allimg} />
    </>
  );
};

export default Image;
