import React from "react";
import Header from "./Header";

const EditNotes = () => {
  return (
    <>
      <Header />
      <div className="main_note" onDoubleClick={backToNormal}>
        <form>
          {open ? (
            <input
              type="text"
              name="title"
              value={note.title}
              onChange={inputEvent}
              placeholder="Title"
              autoComplete="off"
            />
          ) : null}
          <textarea
            type="text"
            placeholder="Write  a note..."
            name="content"
            value={note.content}
            // onChange={inputEvent}
            // onClick={setExpand}

            // rows=""
            // column=""
          ></textarea>
          {open ? (
            <Button onClick={addEvent}>
              <AddIcon className="plus_sign" />
            </Button>
          ) : null}
        </form>
      </div>
    </>
  );
};

export default EditNotes;
