import EditIcon from "@material-ui/icons//Edit";
import { useState } from "react";

const EditNote = () => {
  const [note1, setNote1] = useState({
    title: "",
    content: "",
  });
  const [addItem1, setAddItem1] = useState();

  const inputEvent = (event) => {
    const { name, value } = event.target;
    setNote1((oldData) => {
      return {
        ...oldData,
        [name]: value,
      };
    });
  };
