import React, { useState, useEffect } from "react";
import Header from "./Header";
import Footer from "./Footer";
import CreateNote from "./CreateNote";
import Note from "./Note";
const App = () => {
  const getLocalStorage = () => {
    let list = localStorage.getItem("lists1");
    // console.log(list);
    if (list) {
      return JSON.parse(localStorage.getItem("lists1"));
    } else {
      return [];
    }
  };
  const [addItem, setAddItem] = useState(getLocalStorage([]));
  const addNote = (note) => {
    // alert("i m");
    setAddItem((oldData) => {
      return [...oldData, note];
    });
  };
  const deleteEvent = (id) => {
    setAddItem((prevData) => {
      return prevData.filter((curval, index) => {
        // console.log(curval);
        return index !== id;
      });
    });
  };
  const editEvent = (id) => {
    // alert("hello");
    const editValue = addItem.find((elem) => {
      return elem.id == id;
    });
    setAddItem(editValue);
  };
  useEffect(() => {
    localStorage.setItem("lists1", JSON.stringify(addItem));
  }, [addItem]);
  return (
    <>
      <Header />
      <CreateNote passNote={addNote} />
      {addItem.map((curval, index) => {
        return (
          <Note
            key={index}
            id={index}
            title={curval.title}
            content={curval.content}
            deleData={deleteEvent}
            editData={editEvent}
          />
        );
      })}

      <Footer />
    </>
  );
};

export default App;
