import React from "react";
import Header from "./Header";
import Button from "@material-ui/core/Button";
import AddIcon from "@material-ui/icons/Add";
import DeleteOutlineIcon from "@material-ui/icons//DeleteOutline";
import EditIcon from "@material-ui/icons//Edit";
import { useState } from "react";

const EditNote = () => {
  const [note, setNote] = useState({
    title: "",
    content: "",
  });
  const [addItem, setAddItem] = useState([]);
  const [toogle, setToggle] = useState(true);
  const [editData, setEditData] = useState(null);

  const inputEvent = (event) => {
    const { name, value } = event.target;
    setNote((oldData) => {
      return {
        ...oldData,
        [name]: value,
      };
    });
  };
  const addEvent = () => {
    setAddItem((oldData) => {
      const alldata = {
        id: new Date().getTime().toString(),
      };
      return [...oldData, note];
    });
    setNote({
      title: "",
      content: "",
    });
  };
  const deleteEvent = (id) => {
    // console.log(props);
    setAddItem((preData) => {
      return preData.filter((elem, index) => {
        console.log(index);
        return index !== id;
      });
    });
  };
  const editEvent = (id) => {
    const editData = addItem.find((elem) => {
      console.log(editData);
      return elem.id === id;
    });
    setNote(editData);
    setEditData(id);
    setToggle(false);
  };
  return (
    <>
      <Header />
      <div className="main_note">
        <form>
          <input
            type="text"
            name="title"
            value={note.title}
            onChange={inputEvent}
            placeholder="Title"
            autoComplete="off"
          />

          <textarea
            type="text"
            placeholder="Write  a note..."
            name="content"
            value={note.content}
            onChange={inputEvent}
            // onClick={setExpand}

            // rows=""
            // column=""
          ></textarea>
          {toogle ? (
            <Button onClick={addEvent}>
              <AddIcon className="plus_sign" />
            </Button>
          ) : (
            <button className="btn" onClick={() => editEvent(id)}>
              <EditIcon />
            </button>
          )}
        </form>
      </div>
      {addItem.map((curVal, id) => {
        return (
          <div className="note" key={id}>
            <h1>{curVal.title}</h1>
            <br />
            <p>{curVal.content}</p>
            <button className="btn" onClick={() => deleteEvent(id)}>
              <DeleteOutlineIcon className="deleteIcon" />
            </button>
            <button className="btn" onClick={() => editEvent(id)}>
              <EditIcon />
            </button>
          </div>
        );
      })}
    </>
  );
};

export default EditNote;
