import React, { useState } from "react";

const Image = () => {
  const [img, setImg] = useState(null);
  const inputEvent = (event) => {
    setImg(event.target.files[0]);
  };
  return (
    <>
      <input
        accept="image/*"
        type="file"
        id="select-image"
        onChange={inputEvent}
        name="image"
      />
      <img src={img} />
    </>
  );
};

export default Image;
