import React from "react";
import Header from "./Header";
import Button from "@material-ui/core/Button";
import AddIcon from "@material-ui/icons/Add";
import DeleteOutlineIcon from "@material-ui/icons//DeleteOutline";
import EditIcon from "@material-ui/icons//Edit";
import Footer from "./Footer";
import { useState, useEffect } from "react";

const EditNote = () => {
  const getLocalStorage = () => {
    let list = localStorage.getItem("lists4");
    // console.log(list);
    if (list) {
      return JSON.parse(localStorage.getItem("lists4"));
    } else {
      return [];
    }
  };
  const [note, setNote] = useState({
    title: "",
    content: "",
  });
  const [addItem, setAddItem] = useState(getLocalStorage([]));
  const [toogle, setToggle] = useState(true);
  const [editData, setEditData] = useState(null);
  const [open, setOpen] = useState(false);

  const inputEvent = (event) => {
    const { name, value } = event.target;
    setNote((oldData) => {
      return {
        ...oldData,
        [name]: value,
      };
    });
  };
  const addEvent = () => {
    if (!note) {
      alert("please Enter the Valid Data");
    } else if (note && !toogle) {
      setAddItem(
        addItem.map((elem) => {
          if (elem.id === editData) {
            return { ...elem, name: note };
          }
          return elem;
        })
      );
      setToggle(true);
      setNote({
        title: "",
        content: "",
      });
      setEditData(null);
      setOpen(false);
    } else {
      setAddItem((oldData) => {
        const allData = { id: new Date().getTime().toString(), name: note };
        return [...oldData, allData];
      });
      setNote({
        title: "",
        content: "",
      });
    }
  };

  const deleteEvent = (id) => {
    setAddItem((preData) => {
      return preData.filter((elem) => {
        return id !== elem.id;
      });
    });
  };
  const editEvent = (id) => {
    const editData = addItem.find((elem) => {
      return elem.id === id;
    });
    // console.log(editData);
    setToggle(false);
    setNote(editData.name);
    setEditData(id);
    setOpen(true);
  };
  const setExpand = () => {
    setOpen(true);
  };
  const backToNormal = () => {
    setOpen(false);
  };
  useEffect(() => {
    localStorage.setItem("lists4", JSON.stringify(addItem));
  }, [addItem]);
  return (
    <>
      <Header />
      <div className="main_note" onDoubleClick={backToNormal}>
        <form>
          {open ? (
            <input
              type="text"
              name="title"
              value={note.title}
              onChange={inputEvent}
              placeholder="Title"
              autoComplete="off"
            />
          ) : null}

          <textarea
            type="text"
            placeholder="Write  a note..."
            name="content"
            value={note.content}
            onChange={inputEvent}
            onClick={setExpand}

            // rows=""
            // column=""
          ></textarea>
          {toogle ? (
            <Button title="AddNote" onClick={addEvent}>
              <AddIcon className="plus_sign" />
            </Button>
          ) : (
            <Button onClick={addEvent}>
              <EditIcon className="plus_sign" />
            </Button>
          )}
        </form>
      </div>
      {addItem.map((curVal) => {
        return (
          <div className="note" key={curVal.id}>
            <h1>{curVal.name.title}</h1>
            <br />
            <p>{curVal.name.content}</p>
            <button
              className="btn"
              title="DeleteItem"
              onClick={() => deleteEvent(curVal.id)}
            >
              <DeleteOutlineIcon className="deleteIcon" />
            </button>
            <button
              className="btnEdit"
              title="EditNote"
              onClick={() => editEvent(curVal.id)}
            >
              <EditIcon className="editIcon" />
            </button>
          </div>
        );
      })}
      <Footer />
    </>
  );
};

export default EditNote;
